mapper.select:
select member_nick, auc_no
	from hf_like
where member_nick = #{memberNick} and auc_no = #{aucNo}

mapper.update:
update hf_auc_goods 
	set like_cnt = like_cnt - 1
where no = #{no}

update hf_auc_goods 
	set like_cnt = like_cnt + 1
where no = #{no}

update hf_auc_goods 
	set view_cnt = view_cnt + 1
where no = #{no}

update hf_auc_goods 
	set purchase_confirm = '확정'
where no = #{aucNo}

update hf_auc_goods 
	set refund_status = '환급완료'
where no = #{aucNo}

mapper.select:

select no, auc_no as aucNo, save_name as saveName, ori_name as oriName, file_size as fileSize
 from hf_goods_photo
 where auc_no = #{aucNo}
